const PosixMQ = require('./lib/index');
const mq = new PosixMQ();
mq.open({
    name: '/pmqtest',
    create: true,
    mode: '0777',
    maxmsgs: 10,
    msgsize: 256
});


console.log('mq opened');
console.log('--------------------------------------');

for (let i=0; i<5; i++) {
	mq.push("test-write-"+i);
}
console.log("sent messages.");

mq.close();
